INSERT INTO public.study (id, name, napdi_identifier, overall_summary, pubmed_id, embase_id, cro_identifier, cro_information, date_start, date_end, internal_comment, natural_product_id, study_source_type_id, status) VALUES (28, 'Time-dependent inhibition of CYP2C9, CYP2D6 and CYP3A4 activities in HLM by goldenseal constituents: IC50 shift substrate cocktail assay', 'DMC-UW-001', 'Berberine is a mechanism-based inhibitor of CYP2D6 and CYP3A4, and also reversibly activates the latter enzyme. (-)-Beta-hydrastine is a mechanism-based inhibitor of CYP2C9, CYP2D6 and CYP3A4. Hydrastinine is a mechanism-based inhibitor of CYP2D6, but does not inhibit CYP2C9 or CYP3A4.', null, null, 'DMC-UW', 'Rettie Lab, Dept. of Medicinal Chemistry, Univ. of Washington', null, null, 'This <em>in vitro</em> study was submitted via NaPDI forms from Dr. Allan Rettie''s Lab. Data was curated by JS and reviewed by JY.', 2, 3, 'published');
INSERT INTO public.study (id, name, napdi_identifier, overall_summary, pubmed_id, embase_id, cro_identifier, cro_information, date_start, date_end, internal_comment, natural_product_id, study_source_type_id, status) VALUES (13, 'Inhibitory effects of commonly used herbal extracts on UGT1A1 enzyme activity.', 'PMID:20666626', 'A panel of nine commonly used herbal supplements were screened for their potential to inhibit UGT1A1 activity using human liver microsomes. The result of green tea constituient epigallocatechin gallate (EGCG) was entered. Based on the in vitro findings, inhibition of UGT1A1 by EGCG is plausible, particularly in the intestine where higher extract concentrations are anticipated. Further clinical studies are warranted.', 20666626, null, 'University of Florida', 'Department of Pharmacotherapy and Translational Reserach, University of Florida', null, null, 'This is a study reported in the literature. The data was entered by JY and reviewed by JS.', 1, 1, 'published');
